INSERT INTO comment_group_drift VALUES(198,237,1,replace('{\n  "has_drift": true,\n  "drift_topics": [\n    {\n      "topic": "Инструменты для обнаружения секретов в коде: secretlint и snyk.io",\n      "keywords": ["secretlint", "snyk.io", "линтер", "секреты", "креды", "пайплайн", "проверка перед коммитом", "security"],\n      "key_phrases": ["есть линтер для таких штук", "secretlint", "в пайплайне настраивается довольно просто", "агент прикрутит и в правила проекта добавить проверять перед коммитом", "snyk.io в помощь"],\n      "context": "Обсуждение специализированных инструментов для автоматического обнаружения секретов и credentials в коде - secretlint (линтер для проверки перед коммитом) и snyk.io (платформа для security scanning). Эти инструменты могли бы предотвратить проблему описанную в посте про утечку кредов."\n    },\n    {\n      "topic": "Cursor ignore файлы для управления индексацией",\n      "keywords": ["Cursor", ".cursorignore", ".cursorindexignore", "игнорировать файлы", "индексация", "большие json файлы"],\n      "key_phrases": ["В cursor есть .cursorignore и .cursorindexignore файлы", "можно прописать какие именно файлы игнорировать", "он будет игнорировать их при индексации"],\n      "context": "Обсуждение функциональности Cursor IDE для управления тем, какие файлы должны быть проигнорированы при индексации кодовой базы (аналог .gitignore). Особенно полезно для исключения больших JSON файлов и других файлов, которые не нужно индексировать для AI-ассистента."\n    }\n  ]\n}','\n',char(10)),'2025-10-12 23:50:04','drift-agent (claude-sonnet-4.5)','ai_architect');
